    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

        int contador = 0;
        String d[] = new String[5];

        d[0] = NombrePago1.getText().trim();
        d[1] = NombrePago2.getText().trim();
        d[2] = NombrePago3.getText().trim();
        //d[3] = .getText().trim();
        d[4] = Concepto.getText().trim();


        /*if(d[3].equals("")){
            NombrePago.setBackground(Color.red);
            NombrePago.setForeground(Color.WHITE);
            contador++;
        }*/
        if (d[1].equals("")) {
            NombrePago2.setBackground(Color.red);
            NombrePago2.setForeground(Color.WHITE);
            contador++;
        }
        if (d[2].equals("")) {
            NombrePago3.setBackground(Color.red);
            NombrePago3.setForeground(Color.WHITE);
            contador++;
        }
        if (d[0].equals("")) {
            NombrePago1.setBackground(Color.red);
            NombrePago1.setForeground(Color.WHITE);
            contador++;
        }
        if (d[4].equals("")) {
            Concepto.setBackground(Color.red);
            Concepto.setForeground(Color.WHITE);
            contador++;
        }

        if (contador == 0 && d[2].equals(Login.Contrase単a)) {

            try {
                //coneccion con base de datos
                Connection on = ClaseMetodos.conectar();

                //esta linea de codigo le da las intrucciones a la base de datos
                PreparedStatement pst = on.prepareStatement(
                        "select * from usuarios where Nombres ='" + d[0] + "'");

                ResultSet rs = pst.executeQuery();

                if (rs.next()) {
                    contador++;
                }

                //para evitar que continue con las instrucciones se
                //comprueba que contador sea 0.
                if (contador == 0) {

                    //conexxion con la base de datos
                    Connection cn2 = Clases.ClaseMetodos.conectar();
                    //se dan las espesificaciones a la base de datos mysql
                    PreparedStatement pst2 = cn2.prepareStatement("insert into usuarios values "
                            + "(?,?,?, ?,?)");//5

                    pst2.setInt(1, 0);
                    pst2.setString(2, d[0]);
                    pst2.setString(3, Login.Usuario);
                    pst2.setString(4, d[4]);
                    pst2.setString(5, d[1]);

                    pst2.executeUpdate();
                    cn2.close();
                    
                    JOptionPane.showMessageDialog(null,"Registrado el usuario "+d[0]);
                    NombrePago1.setText("");
                    NombrePago2.setText("");
                    NombrePago3.setText("");
                    Concepto.setText("");
                } else {
                    
                    JOptionPane.showMessageDialog(null, "Este usuario ya se encuentra registrado");
                    
                }
            } catch (SQLException e) {

                System.out.println("este " + e);

            }

        } else {
            JOptionPane.showMessageDialog(null, "Revise los campos y su contrase単a");
        }

    }//GEN-LAST:event_jButton1ActionPerformed

